import { discount, robot } from "../assets";

const Hero = () => {
  return (
    <div>
      <section
        id="home"
        className={`flex md:flex-row flex-col ${styles.paddingY}`}
      >
        <div
          className={`flex-1 ${styles.flexStart} flex-col xl:px-0 sm:px-6 px-6`}
        >
          <div className="flex flex-row items-center py-[6px] px-4 bg-discount-gradient rounded-[10px] mb-2 ">
            <img src={discount} alt="discount" className="w-[32px] h-[32px]" />
            <p className={`${styles.paragraph} ml-2`}>
              <span className="text-white"> 20%</span> Discount For{" "}
              <span className="text-white"> 1 Month</span> Acount
            </p>
          </div>

          <div className="flex flex-row justify-between items-centers w-full">
            <h1 className="flex-1 font-poppins font-semibold ss:text-[72px] text-[52px] text-white">
              {" "}
              The Most <br className="sm:block hidden" />{" "}
              <span className="text-gradient">Secure Method</span> For Your
              transaction.
            </h1>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Hero;
